CREATE VIEW VW_PROYECCION_VENTAS_ANO11 (razonsocial, Id_Proyecto, NomProyecto, Periodo, Precio, Unidades) AS SELECT T1.razonsocial, T2.Id_Proyecto, T2.NomProyecto, T4.Periodo, T4.Precio, Sum(T5.Unidades) FROM Empresa T1, Proyecto T2, ProyectoProducto T3, ProyectoProductoPrecio T4, ProyectoProductoUnidadesVentas T5 WHERE T1.codproyecto=T2.Id_Proyecto AND T2.Id_Proyecto=T3.CodProyecto AND T4.CodProducto=T3.Id_Producto AND T5.CodProducto=T3.Id_Producto AND T4.Periodo=T5.Ano AND T4.Periodo=1 GROUP BY T1.razonsocial, T2.Id_Proyecto, T2.NomProyecto, T4.Periodo, T4.Precio