CREATE VIEW VW_PRUEBAFRANCO (Nombres, Apellidos, Identificacion, Email, CodTipoAprendiz, CodConvocatoria, CodProyecto, Viable, CodRol) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.CodTipoAprendiz, T3.CodConvocatoria, T3.CodProyecto, T3.Viable, T5.CodRol FROM Contacto T1, Convocatoria T2, ConvocatoriaProyecto T3, Proyecto T4, ProyectoContacto T5 WHERE T4.CodContacto=T5.CodContacto AND T3.CodProyecto=T4.Id_Proyecto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodConvocatoria=113 AND T5.CodRol=3