CREATE VIEW VW_PRUEBAFRANCO2 (NomCiudad, Nombres, Apellidos, Identificacion, Email, CodConvocatoria, Viable, NomDepartamento, NomUnidad, Id_Proyecto, NomProyecto, CodRol, cc) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T3.CodConvocatoria, T3.Viable, T4.NomDepartamento, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.CodRol, T7.CodConvocatoria FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Institucion T5, Proyecto T6, ProyectoContacto T7 WHERE T2.Id_Contacto=T7.CodContacto AND T6.CodCiudad=T1.Id_Ciudad AND T3.CodProyecto=T7.CodProyecto AND T3.CodProyecto=T6.Id_Proyecto AND T6.CodInstitucion=T5.Id_Institucion AND T1.CodDepartamento=T4.Id_Departamento AND T3.Viable=1 AND T7.CodRol=3 AND T3.CodConvocatoria<=75 AND T6.CodEstado=7