CREATE VIEW VW_PRUEBAFRANCO3 (Nombres, Apellidos, Identificacion, Email, Id_Convocatoria) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T2.Id_Convocatoria FROM Contacto T1, Convocatoria T2, ConvocatoriaProyecto T3, Proyecto T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T4.Id_Proyecto=T5.CodProyecto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T4.Id_Proyecto=T3.CodProyecto AND T2.Id_Convocatoria=112 AND T5.CodRol=3