CREATE VIEW VW_PY_CAPACITACION (NomDepartamento, Sum_T4_ValorRecomendado, Sum_T6_Recursos) AS SELECT T3.NomDepartamento, Sum(T4.ValorRecomendado), Sum(T6.Recursos) FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Proyecto T5, ProyectoFinanzasIngresos T6 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T5.Id_Proyecto=T2.CodProyecto AND T2.CodConvocatoria=2 AND T5.Id_Proyecto=T6.CodProyecto AND T5.Id_Proyecto=T4.CodProyecto AND T2.Viable=1 AND T4.CodConvocatoria=2 GROUP BY T3.NomDepartamento