CREATE VIEW VW_PY_VIABLES_MONTOSOL_MONTOREC_2DACONV (Viable, NomDepartamento, ValorRecomendado, Id_Proyecto, NomProyecto, Recursos) AS SELECT T2.Viable, T3.NomDepartamento, T4.ValorRecomendado, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Proyecto T5, ProyectoFinanzasIngresos T6 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T5.Id_Proyecto=T2.CodProyecto AND T2.CodConvocatoria=2 AND T5.Id_Proyecto=T6.CodProyecto AND T5.Id_Proyecto=T4.CodProyecto AND T2.Viable=1 AND T4.CodConvocatoria=2