CREATE VIEW VW_PY_VIABLES_MONTOS_UNIDADES (NomDepartamento, ValorRecomendado, NomUnidad, CantidadProyectos, ValorSolicitado) AS SELECT T3.NomDepartamento, Sum(T4.ValorRecomendado), T5.NomUnidad, Count(T6.Id_Proyecto), Sum(T7.Recursos) FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Institucion T5, Proyecto T6, ProyectoFinanzasIngresos T7, TipoInstitucion T8 WHERE T1.CodDepartamento=T3.Id_Departamento AND T6.Id_Proyecto=T2.CodProyecto AND T6.Id_Proyecto=T4.CodProyecto AND T2.CodConvocatoria=2 AND T4.CodConvocatoria=2 AND T6.CodInstitucion=T5.Id_Institucion AND T6.Id_Proyecto=T7.CodProyecto AND T8.Id_TipoInstitucion=T5.CodTipoInstitucion AND T2.Viable=1 AND T1.Id_Ciudad=T5.CodCiudad GROUP BY T3.NomDepartamento, T5.NomUnidad