CREATE VIEW VW_Pagos_C2 (razonsocial, CodActa, Legalizado, Id_PagoActividad, NomPagoActividad, CantidadDinero, Estado, Id_Proyecto, NomProyecto) AS SELECT T1.razonsocial, T3.CodActa, T3.Legalizado, T4.Id_PagoActividad, T4.NomPagoActividad, T4.CantidadDinero, T4.Estado, T5.Id_Proyecto, T5.NomProyecto FROM Empresa T1, LegalizacionActa T2, LegalizacionActaProyecto T3, PagoActividad T4, Proyecto T5 WHERE T1.codproyecto=T5.Id_Proyecto AND T2.Id_Acta=T3.CodActa AND T3.Legalizado=1 AND T2.Id_Acta<=58 AND T4.CodProyecto=T5.Id_Proyecto AND T3.CodProyecto=T5.Id_Proyecto