CREATE VIEW VW_Pagos_Fiduciaria (id_empresa, razonsocial, NomPagoActividad, Tipo, Mes, CantidadDinero, Estado, FechaIngreso, FechaInterventor, FechaCoordinador, FechaRtaFA) AS SELECT T1.id_empresa, T1.razonsocial, T2.NomPagoActividad, T2.Tipo, T2.Mes, T2.CantidadDinero, T2.Estado, T2.FechaIngreso, T2.FechaInterventor, T2.FechaCoordinador, T2.FechaRtaFA FROM Empresa T1, PagoActividad T2 WHERE T1.codproyecto=T2.CodProyecto