CREATE VIEW VW_RECHAZOS_PGOS_INTERVENTOR (Nombres, Apellidos, razonsocial, NoRechazos, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T2.razonsocial, Count(T4.Id_PagoActividad), T5.Id_Proyecto, T5.NomProyecto FROM Contacto T1, Empresa T2, EmpresaInterventor T3, PagoActividad T4, Proyecto T5 WHERE T1.Id_Contacto=T3.CodContacto AND T2.id_empresa=T3.CodEmpresa AND T2.codproyecto=T5.Id_Proyecto AND T4.CodProyecto=T2.codproyecto AND T4.Estado=5 AND T3.Rol=8 GROUP BY T1.Nombres, T1.Apellidos, T2.razonsocial, T5.Id_Proyecto, T5.NomProyecto