CREATE VIEW VW_REGISTROMERCANTIL (NomCiudad, NomConvocatoria, razonsocial, codproyecto, DomicilioEmpresa, Telefono, Email, Nit, TipoRegimen) AS SELECT T1.NomCiudad, T2.NomConvocatoria, T4.razonsocial, T4.codproyecto, T4.DomicilioEmpresa, T4.Telefono, T4.Email, T4.Nit, T4.TipoRegimen FROM Ciudad T1, Convocatoria T2, ConvocatoriaProyecto T3, Empresa T4 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T4.codproyecto AND T3.CodConvocatoria>=32 AND T3.CodConvocatoria<=46