CREATE VIEW VW_REGMERCANTILREPLEGAL (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, CodConvocatoria, CodProyecto, NomDepartamento, razonsocial, CapitalSocial, DomicilioEmpresa, TelEmpresa, MAILEMPRESA, Nit, TipoRegimen, RepresentanteLegal, Suplente) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T3.CodConvocatoria, T3.CodProyecto, T4.NomDepartamento, T5.razonsocial, T5.CapitalSocial, T5.DomicilioEmpresa, T5.Telefono, T5.Email, T5.Nit, T5.TipoRegimen, T6.RepresentanteLegal, T6.Suplente FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Empresa T5, EmpresaContacto T6 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T4.Id_Departamento AND T3.CodProyecto=T5.codproyecto AND T5.id_empresa=T6.codempresa AND T2.Id_Contacto=T6.codcontacto AND T3.CodConvocatoria>=32 AND T3.CodConvocatoria<=42