CREATE VIEW VW_REPLEGAL (Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, razonsocial, RepresentanteLegal, Suplente, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Direccion, T1.Telefono, T3.razonsocial, T4.RepresentanteLegal, T4.Suplente, T5.Id_Proyecto, T5.NomProyecto FROM Contacto T1, ConvocatoriaProyecto T2, Empresa T3, EmpresaContacto T4, Proyecto T5 WHERE T1.Id_Contacto=T4.codcontacto AND T2.CodProyecto=T5.Id_Proyecto AND T3.codproyecto=T5.Id_Proyecto AND T3.id_empresa=T4.codempresa AND T2.CodConvocatoria>=32 AND T2.CodConvocatoria<=42