CREATE VIEW VW_REPLEGAL1 (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomConvocatoria, NomDepartamento, razonsocial, CapitalSocial, DomicilioEmpresa, tel, MailEmpresa, Nit, TipoRegimen, RepresentanteLegal, Suplente) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T3.NomConvocatoria, T5.NomDepartamento, T6.razonsocial, T6.CapitalSocial, T6.DomicilioEmpresa, T6.Telefono, T6.Email, T6.Nit, T6.TipoRegimen, T7.RepresentanteLegal, T7.Suplente FROM Ciudad T1, Contacto T2, Convocatoria T3, ConvocatoriaProyecto T4, departamento T5, Empresa T6, EmpresaContacto T7 WHERE T1.Id_Ciudad=T6.CodCiudad AND T1.CodDepartamento=T5.Id_Departamento AND T2.Id_Contacto=T7.codcontacto AND T3.Id_Convocatoria=T4.CodConvocatoria AND T4.CodProyecto=T6.codproyecto AND T6.id_empresa=T7.codempresa AND T4.CodConvocatoria<=45 AND T4.CodConvocatoria>=34