CREATE VIEW VW_REPORTEACREDITACIONCONVOCATORIAS (Id_Proyecto, EstadoAcreditacion, EstadoProyecto, Id_Convocatoria, NomConvocatoria) AS SELECT T1.Id_Proyecto, T1.EstadoAcreditacion, T1.EstadoProyecto, T2.Id_Convocatoria, T2.NomConvocatoria FROM EstadoProyectoAcreditacion T1, Convocatoria T2 WHERE T1.CodConvocatoria=T2.Id_Convocatoria AND T1.CodConvocatoria>=141 AND T1.CodConvocatoria<=147