CREATE VIEW VW_REPRESENTANTELEGAL (Nombres, Apellidos, Identificacion, Email, razonsocial, Nit, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T2.razonsocial, T2.Nit, T4.Id_Proyecto, T4.NomProyecto FROM Contacto T1, Empresa T2, EmpresaContacto T3, Proyecto T4 WHERE T1.Id_Contacto=T3.codcontacto AND T2.id_empresa=T3.codempresa AND T2.codproyecto=T4.Id_Proyecto AND T3.RepresentanteLegal=1