CREATE VIEW VW_REPRESENTANTESLEGALESXEMPRESA (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomDepartamento, razonsocial, ObjetoSocial, DomicilioEmpresa, TelEmpresa, MailEmpresa, Nit, RepresentanteLegal, Id_Proyecto, NomProyecto, Sumario, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T3.NomDepartamento, T4.razonsocial, T4.ObjetoSocial, T4.DomicilioEmpresa, T4.Telefono, T4.Email, T4.Nit, T5.RepresentanteLegal, T6.Id_Proyecto, T6.NomProyecto, T6.Sumario, T7.NomSector, T8.NomSubSector FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, EmpresaContacto T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T4.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.Id_Contacto=T5.codcontacto AND T4.codproyecto=T6.Id_Proyecto AND T4.id_empresa=T5.codempresa AND T6.CodSubSector=T8.Id_SubSector AND T8.CodSector=T7.Id_Sector AND T5.RepresentanteLegal=1