CREATE VIEW VW_REP_LEGALES (NomCiudad, Nombres, Apellidos, Identificacion, NomDepartamento, razonsocial, CapitalSocial, Nit, TipoRegimen, RepresentanteLegal, Suplente, Id_Proyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T3.NomDepartamento, T4.razonsocial, T4.CapitalSocial, T4.Nit, T4.TipoRegimen, T5.RepresentanteLegal, T5.Suplente, T6.Id_Proyecto FROM Ciudad T1, Contacto T2, departamento T3, Empresa T4, EmpresaContacto T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Contacto=T5.codcontacto AND T3.Id_Departamento=T1.CodDepartamento AND T4.id_empresa=T5.codempresa AND T4.codproyecto=T6.Id_Proyecto AND T6.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector