CREATE VIEW VW_REVISAR_VALORES_APORTES (CodConvocatoria, Id_Estado, Nombre, Detalle, suma_solicitado, Recomendado, Id_Proyecto, NomProyecto) AS SELECT T1.CodConvocatoria, T2.Id_Estado, T3.Nombre, T3.Detalle, Sum(T3.Solicitado), T3.Recomendado, T4.Id_Proyecto, T4.NomProyecto FROM ConvocatoriaProyecto T1, Estado T2, EvaluacionProyectoAporte T3, Proyecto T4 WHERE T1.CodProyecto=T4.Id_Proyecto AND T2.Id_Estado=T4.CodEstado AND T3.CodProyecto=T4.Id_Proyecto GROUP BY T1.CodConvocatoria, T2.Id_Estado, T3.Nombre, T3.Detalle, T3.Recomendado, T4.Id_Proyecto, T4.NomProyecto