CREATE VIEW VW_ROL1 (Ciudad, Nombres, Apellidos, Email, Departamento, Unidad, Id_Proyecto, NomProyecto, Nombre) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T3.NomDepartamento, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T7.Nombre FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoContacto T6, Rol T7 WHERE T1.CodDepartamento=T3.Id_Departamento AND T2.CodCiudad=T1.Id_Ciudad AND T2.CodInstitucion=T4.Id_Institucion AND T2.Id_Contacto=T6.CodContacto AND T7.Id_Rol=T6.CodRol AND T6.CodProyecto=T5.Id_Proyecto