CREATE VIEW VW_SEGUNDACONVOCATORIAENEJECUCION (razonsocial, Id_Proyecto, NomProyecto, CodEstado) AS SELECT T3.razonsocial, T4.Id_Proyecto, T4.NomProyecto, T4.CodEstado FROM Convocatoria T1, ConvocatoriaProyecto T2, Empresa T3, Proyecto T4 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T4.Id_Proyecto AND T3.codproyecto=T4.Id_Proyecto AND T1.encargofiduciario=103937 AND T2.CodConvocatoria=2 AND T4.CodEstado=7