CREATE VIEW VW_SENAJEFESUNIDAD (NomCiudad, Nombres, Apellidos, Email, Telefono, NomDepartamento, NomInstitucion, NomUnidad, Direccion, telefonoinst) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Telefono, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T4.Direccion, T4.Telefono FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, InstitucionContacto T5, Proyecto T6, Sector T7 WHERE T1.Id_Ciudad=T4.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.Id_Contacto=T5.CodContacto AND T5.CodInstitucion=T4.Id_Institucion AND T6.CodInstitucion=T4.Id_Institucion