CREATE VIEW VW_SENA_CAPACITACIONPROYECTOS2CONV (NomCiudad, NomDepartamento, ValorRecomendado, Id_Proyecto, NomProyecto, Sumario) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.ValorRecomendado, T5.Id_Proyecto, T5.NomProyecto, T5.Sumario FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Proyecto T5 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T5.Id_Proyecto=T4.CodProyecto AND T5.Id_Proyecto=T2.CodProyecto AND T2.CodConvocatoria=2 AND T2.Viable=1 AND T4.CodConvocatoria=2