CREATE VIEW VW_SENA_CONSULTA1 (NomCiudad, CodConvocatoria, NomDepartamento, Id_Proyecto, NomProyecto, Sumario, CodEstado, NomSector) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T3.NomDepartamento, T4.Id_Proyecto, T4.NomProyecto, T4.Sumario, T4.CodEstado, T5.NomSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4, Sector T5, SubSector T6 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T2.CodProyecto=T4.Id_Proyecto AND T4.CodSubSector=T6.Id_SubSector AND T5.Id_Sector=T6.CodSector AND T4.CodEstado>=3 AND T2.CodConvocatoria<=2