CREATE VIEW VW_SENA_NOPLANESXASESORXUNIDADEMPRENDIMIENTO (Nombres, Apellidos, Identificacion, Email, Clave, NomInstitucion, NomUnidad, NoProyectos, CodRol) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Clave, T2.NomInstitucion, T2.NomUnidad, Count(T3.Id_Proyecto), T4.CodRol FROM Contacto T1, Institucion T2, Proyecto T3, ProyectoContacto T4 WHERE T1.Id_Contacto=T4.CodContacto AND T2.Id_Institucion=T3.CodInstitucion AND T3.Id_Proyecto=T4.CodProyecto AND T4.CodRol<=2 AND T4.Inactivo=0 AND T3.CodEstado>=7 GROUP BY T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Clave, T2.NomInstitucion, T2.NomUnidad, T4.CodRol