CREATE VIEW VW_SENA_PLANESSECTORAGORYPESCA (Generado, NomCiudad, NomDepartamento, ValorRecomendado, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, NomSector, NomSubSector) AS SELECT T1.Generado, T2.NomCiudad, T3.NomDepartamento, T4.ValorRecomendado, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.NomSector, T8.NomSubSector FROM EmpleosGenerados T1, Ciudad T2, departamento T3, EvaluacionObservacion T4, Institucion T5, Proyecto T6, Sector T7, SubSector T8 WHERE T1.CodProyecto=T6.Id_Proyecto AND T2.Id_Ciudad=T6.CodCiudad AND T2.CodDepartamento=T3.Id_Departamento AND T4.CodProyecto=T6.Id_Proyecto AND T5.Id_Institucion=T6.CodInstitucion AND T8.Id_SubSector=T6.CodSubSector AND T8.CodSector=T7.Id_Sector AND T6.CodEstado>=7 AND T7.Id_Sector<=2