CREATE VIEW VW_SENA_PROYECTOSXUNIDAD (NomCiudad, NomDepartamento, NomInstitucion, NomUnidad, No_Proyectos) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.NomInstitucion, T3.NomUnidad, Count(T4.Id_Proyecto) FROM Ciudad T1, departamento T2, Institucion T3, Proyecto T4 WHERE T3.Id_Institucion=T4.CodInstitucion AND T4.CodEstado>=7 AND T1.Id_Ciudad>=T3.CodCiudad AND T1.CodDepartamento=T2.Id_Departamento GROUP BY T1.NomCiudad, T2.NomDepartamento, T3.NomInstitucion, T3.NomUnidad