CREATE VIEW VW_TAREASAGENDADAS (Nombres, Apellidos, NomGrupo, NomTareaPrograma, Descripcion, Fecha, Respuesta, FechaCierre) AS SELECT T1.Nombres, T1.Apellidos, T2.NomGrupo, T4.NomTareaPrograma, T5.Descripcion, T6.Fecha, T6.Respuesta, T6.FechaCierre FROM Contacto T1, Grupo T2, GrupoContacto T3, TareaPrograma T4, TareaUsuario T5, TareaUsuarioRepeticion T6 WHERE T1.Id_Contacto=T3.CodContacto AND T3.CodGrupo=T2.Id_Grupo AND T1.Id_Contacto=T5.CodContactoAgendo AND T4.Id_TareaPrograma=T5.CodTareaPrograma AND T5.Id_TareaUsuario=T6.CodTareaUsuario AND T2.Id_Grupo=11 AND T6.Fecha>=05/30/2005