CREATE VIEW VW_TAREASAGENDADAS_ICONO (Nombres, Apellidos, NomGrupo, NomTareaPrograma, Descripcion, Fecha, Respuesta, FechaCierre) AS SELECT T1.Nombres, T1.Apellidos, T3.NomGrupo, T5.NomTareaPrograma, T6.Descripcion, T7.Fecha, T7.Respuesta, T7.FechaCierre FROM Contacto T1, ConvocatoriaProyecto T2, Grupo T3, GrupoContacto T4, TareaPrograma T5, TareaUsuario T6, TareaUsuarioRepeticion T7 WHERE T1.Id_Contacto=T4.CodContacto AND T3.Id_Grupo=T4.CodGrupo AND T2.CodConvocatoria=2 AND T2.CodProyecto=T6.CodProyecto AND T1.Id_Contacto=T6.CodContactoAgendo AND T5.Id_TareaPrograma=T6.CodTareaPrograma AND T6.Id_TareaUsuario=T7.CodTareaUsuario AND T3.Id_Grupo=11