CREATE VIEW VW_TAREAS_ASESOR_AGENDADAS (NomInstitucion, NomUnidad, Id_Proyecto, CodContacto, Descripcion, Fecha, Respuesta, FechaCierre) AS SELECT T1.NomInstitucion, T1.NomUnidad, T2.Id_Proyecto, T4.CodContacto, T4.Descripcion, T5.Fecha, T5.Respuesta, T5.FechaCierre FROM Institucion T1, Proyecto T2, ProyectoContacto T3, TareaUsuario T4, TareaUsuarioRepeticion T5 WHERE T1.Id_Institucion=T2.CodInstitucion AND T2.Id_Proyecto=T3.CodProyecto AND T4.Id_TareaUsuario=T5.CodTareaUsuario AND T3.CodRol<=2 AND T3.CodContacto=T4.CodContacto AND T2.Id_Proyecto=33438