CREATE VIEW VW_TAREAS_EMPRENDEDOR_AGENDADAS (NomInstitucion, NomUnidad, Tareas_Agendadas) AS SELECT T1.NomInstitucion, T1.NomUnidad, Count(T5.Id_TareaUsuarioRepeticion) FROM Institucion T1, Proyecto T2, ProyectoContacto T3, TareaUsuario T4, TareaUsuarioRepeticion T5 WHERE T1.Id_Institucion=T2.CodInstitucion AND T2.Id_Proyecto=T3.CodProyecto AND T4.Id_TareaUsuario=T5.CodTareaUsuario AND T4.CodProyecto=T3.CodProyecto AND T3.CodRol=3 GROUP BY T1.NomInstitucion, T1.NomUnidad