CREATE VIEW VW_TAREAS_INSTITUCION_PRUEBA (NomInstitucion, NomUnidad, Id_TareaUsuario, Id_TareaUsuarioRepeticion, Fecha, CodTareaUsuario, Respuesta, FechaCierre) AS SELECT T1.NomInstitucion, T1.NomUnidad, T4.Id_TareaUsuario, T5.Id_TareaUsuarioRepeticion, T5.Fecha, T5.CodTareaUsuario, T5.Respuesta, T5.FechaCierre FROM Institucion T1, Proyecto T2, ProyectoContacto T3, TareaUsuario T4, TareaUsuarioRepeticion T5 WHERE T1.Id_Institucion=T2.CodInstitucion AND T2.Id_Proyecto=T3.CodProyecto AND T4.Id_TareaUsuario=T5.CodTareaUsuario AND T4.CodProyecto=T3.CodProyecto AND T3.CodRol<=2