CREATE VIEW VW_TAREA_POR_CONV (Id_Convocatoria, NomConvocatoria, Descripcion, FechaInicio, FechaFin, Presupuesto, MinimoPorPlan, CodContacto, Publicado, encargofiduciario, CodConvocatoria, CodProyecto, Fecha, Justificacion, Viable, codevaluacionconceptos, Id_TareaUsuario, CodTareaPrograma, contacto, idproy, NomTareaUsuario, detalle, Recurrente, RecordatorioEmail, NivelUrgencia, RecordatorioPantalla, RequiereRespuesta, CodContactoAgendo, DocumentoRelacionado) AS SELECT * FROM Convocatoria T1, ConvocatoriaProyecto T2, TareaUsuario T3 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T3.CodProyecto AND T2.CodConvocatoria=112