CREATE VIEW VW_TELEFONOSEMPRENDEDORES (Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, Clave, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Direccion, T1.Telefono, T1.Clave, T2.Id_Proyecto, T2.NomProyecto FROM Contacto T1, Proyecto T2, ProyectoContacto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Proyecto=T3.CodProyecto AND T3.CodRol=3 AND T3.Inactivo=0