CREATE VIEW VW_TIPO_APRENDIZ_PROY_TODASCONV (NomCiudad, NomDepartamento, Id_Proyecto, NomProyecto, Sumario, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomTipoAprendiz) AS SELECT T1.NomCiudad, T1.NomDepartamento, T1.Id_Proyecto, T1.NomProyecto, T1.Sumario, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T3.NomTipoAprendiz FROM Conv_Avalados_General T1, Contacto T2, TipoAprendiz T3 WHERE T1.Identificacion=T2.Identificacion AND T2.CodTipoAprendiz=T3.Id_TipoAprendiz AND T1.CodRol=3 AND T3.Id_TipoAprendiz=3