CREATE VIEW VW_UNIDADES_DE_EMPRENDIMIENTO (Nombres, Apellidos, Email, Id_Institucion, NomInstitucion, NomUnidad, Direccion, Telefono, Inactivo, MotivoCambioJefe) AS SELECT T1.Nombres, T1.Apellidos, T1.Email, T2.Id_Institucion, T2.NomInstitucion, T2.NomUnidad, T2.Direccion, T2.Telefono, T2.Inactivo, T2.MotivoCambioJefe FROM Contacto T1, Institucion T2, InstitucionContacto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Institucion=T3.CodInstitucion AND T2.Inactivo=0 AND T1.Inactivo=0 AND T1.CodInstitucion=T2.Id_Institucion