CREATE VIEW VW_UNIDADES_EMPRENDIMIENTO (NomCiudad, Nombres, Apellidos, Identificacion, Cargo, CodInstitucion, Clave, NomDepartamento, NomInstitucion) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Cargo, T2.CodInstitucion, T2.Clave, T3.NomDepartamento, T4.NomInstitucion FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4 WHERE T1.Id_Ciudad=T2.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.CodInstitucion=T4.Id_Institucion