CREATE VIEW VW_V2_DNP (Id_Ciudad, NomCiudad, Id_Departamento, NomDepartamento, razonsocial, ObjetoSocial, DomicilioEmpresa, Telefono, Email, Nit, ValorRecomendado, AporteRecomendado, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Id_Sector, NomSector) AS SELECT T1.Id_Ciudad, T1.NomCiudad, T2.Id_Departamento, T2.NomDepartamento, T3.razonsocial, T3.ObjetoSocial, T3.DomicilioEmpresa, T3.Telefono, T3.Email, T3.Nit, T4.ValorRecomendado, Sum(T5.Recomendado), T6.NomInstitucion, T6.NomUnidad, T8.Id_Proyecto, T8.NomProyecto, T9.Id_Sector, T9.NomSector FROM Ciudad T1, departamento T2, Empresa T3, EvaluacionObservacion T4, EvaluacionProyectoAporte T5, Institucion T6, PagoActividad T7, Proyecto T8, Sector T9, SubSector T10 WHERE T1.Id_Ciudad=T8.CodCiudad AND T2.Id_Departamento=T1.CodDepartamento AND T3.codproyecto=T8.Id_Proyecto AND T4.CodProyecto=T8.Id_Proyecto AND T5.CodProyecto=T8.Id_Proyecto AND T6.Id_Institucion=T8.CodInstitucion AND T7.CodProyecto=T8.Id_Proyecto AND T10.Id_SubSector=T8.CodSubSector AND T10.CodSector=T9.Id_Sector GROUP BY T1.Id_Ciudad, T1.NomCiudad, T2.Id_Departamento, T2.NomDepartamento, T3.razonsocial, T3.ObjetoSocial, T3.DomicilioEmpresa, T3.Telefono, T3.Email, T3.Nit, T4.ValorRecomendado, T6.NomInstitucion, T6.NomUnidad, T8.Id_Proyecto, T8.NomProyecto, T9.Id_Sector, T9.NomSector