CREATE VIEW VW_VALORES_APROBADOS (NomCiudad, CodConvocatoria, Justificacion, Viable, NomDepartamento, ValorRecomendado, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T2.Justificacion, T2.Viable, T3.NomDepartamento, T4.ValorRecomendado, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.Recursos FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Institucion T5, Proyecto T6, ProyectoFinanzasIngresos T7 WHERE T6.CodCiudad=T1.Id_Ciudad AND T1.CodDepartamento=T3.Id_Departamento AND T4.CodProyecto=T6.Id_Proyecto AND T5.Id_Institucion=T6.CodInstitucion AND T6.Id_Proyecto=T7.CodProyecto AND T4.CodProyecto=T6.Id_Proyecto AND T2.CodProyecto=T6.Id_Proyecto