CREATE VIEW VW_VENTASPROYECTADASYREGISTRADAS (TotalVentas, CodTipoFinanciacion, Valor, encargofiduciario, razonsocial, Id_Proyecto, NomProyecto) AS SELECT T1.TotalVentas, T2.CodTipoFinanciacion, Sum(T2.Valor), T3.encargofiduciario, T5.razonsocial, T7.Id_Proyecto, T7.NomProyecto FROM ProyectoProyeccionesVentasTotal T1, AvanceVentasPOMes T2, Convocatoria T3, ConvocatoriaProyecto T4, Empresa T5, InterventorVentas T6, Proyecto T7 WHERE T1.codproyecto=T7.Id_Proyecto AND T2.CodProducto=T6.id_ventas AND T3.Id_Convocatoria=T4.CodConvocatoria AND T4.CodProyecto=T7.Id_Proyecto AND T5.codproyecto=T7.Id_Proyecto AND T6.CodProyecto=T7.Id_Proyecto AND T3.Id_Convocatoria=103937 AND T2.CodTipoFinanciacion=2 GROUP BY T1.TotalVentas, T2.CodTipoFinanciacion, T3.encargofiduciario, T5.razonsocial, T7.Id_Proyecto, T7.NomProyecto