CREATE VIEW VW_VENTAS_PROYECCIONPO (razonsocial, Id_Proyecto, NomProyecto, Precio, Sum_T5_Unidades) AS SELECT T1.razonsocial, T2.Id_Proyecto, T2.NomProyecto, T4.Precio, Sum(T5.Unidades) FROM Empresa T1, Proyecto T2, ProyectoProducto T3, ProyectoProductoPrecio T4, ProyectoProductoUnidadesVentas T5 WHERE T1.codproyecto=T2.Id_Proyecto AND T2.Id_Proyecto=T3.CodProyecto AND T3.Id_Producto=T4.CodProducto AND T3.Id_Producto=T5.CodProducto AND T5.Ano=1 AND T4.Periodo=T5.Ano GROUP BY T1.razonsocial, T2.Id_Proyecto, T2.NomProyecto, T4.Precio