CREATE VIEW VW_VIABLES_POR_SECTOR (NomCiudad, CodConvocatoria, Viable, NomDepartamento, ValorRecomendado, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T2.Viable, T3.NomDepartamento, T4.ValorRecomendado, T5.NomInstitucion, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T7.Recursos, T8.NomSector, T9.NomSubSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Institucion T5, Proyecto T6, ProyectoFinanzasIngresos T7, Sector T8, SubSector T9 WHERE T1.CodDepartamento=T3.Id_Departamento AND T4.CodProyecto=T6.Id_Proyecto AND T1.Id_Ciudad=T6.CodCiudad AND T5.Id_Institucion=T6.CodInstitucion AND T7.CodProyecto=T6.Id_Proyecto AND T9.Id_SubSector=T6.CodSubSector AND T9.CodSector=T8.Id_Sector AND T2.CodProyecto=T6.Id_Proyecto