CREATE VIEW VW_VISITAS_INTERVENTORIA (Nombres, Apellidos, razonsocial, Id_Proyecto, NomProyecto, FechaInicio, FechaFin, Estado, Objeto) AS SELECT T1.Nombres, T1.Apellidos, T2.razonsocial, T4.Id_Proyecto, T4.NomProyecto, T5.FechaInicio, T5.FechaFin, T5.Estado, T5.Objeto FROM Contacto T1, Empresa T2, EmpresaInterventor T3, Proyecto T4, Visita T5 WHERE T1.Id_Contacto=T3.CodContacto AND T2.id_empresa=T3.CodEmpresa AND T2.codproyecto=T4.Id_Proyecto AND T5.Id_Empresa=T2.id_empresa AND T3.Inactivo=0