CREATE VIEW VW_VISTATAREAS (Nombres, Apellidos, CodProyecto, NomTareaUsuario, Descripcion, CodContactoAgendo, Fecha, Respuesta) AS SELECT T1.Nombres, T1.Apellidos, T2.CodProyecto, T2.NomTareaUsuario, T2.Descripcion, T2.CodContactoAgendo, T3.Fecha, T3.Respuesta FROM Contacto T1, TareaUsuario T2, TareaUsuarioRepeticion T3 WHERE T2.Id_TareaUsuario=T3.CodTareaUsuario AND T2.CodContacto=T1.Id_Contacto AND T2.CodProyecto=28835