CREATE VIEW VW_VISTA_FINAL_SMLV_CON32 (NomCiudad, Fecha, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Fecha, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos, T7.NomSector, T8.NomSubSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoFinanzasIngresos T6, Sector T7, SubSector T8 WHERE T5.Id_Proyecto=T2.CodProyecto AND T2.CodConvocatoria=32 AND T5.CodInstitucion=T4.Id_Institucion AND T5.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector AND T5.CodCiudad=T1.Id_Ciudad AND T1.CodDepartamento=T3.Id_Departamento AND T6.CodProyecto=T5.Id_Proyecto AND T6.CodProyecto=T2.CodProyecto