CREATE VIEW VW_VISTA_JESSI (Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomInstitucion, NomUnidad, Dir_Inst, Tel_Inst, FechaInicio, FechaFin) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Direccion, T1.Telefono, T2.NomInstitucion, T2.NomUnidad, T2.Direccion, T2.Telefono, T3.FechaInicio, T3.FechaFin FROM Contacto T1, Institucion T2, InstitucionContacto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Institucion=T3.CodInstitucion