CREATE VIEW VW_V_ASESORES (NomCiudad, Nombres, Apellidos, Cargo, Email, NomDepartamento, NomInstitucion) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Cargo, T2.Email, T3.NomDepartamento, T6.NomInstitucion FROM Ciudad T1, Contacto T2, departamento T3, Grupo T4, GrupoContacto T5, Institucion T6 WHERE T2.CodCiudad=T1.Id_Ciudad AND T2.CodInstitucion=T6.Id_Institucion AND T2.Id_Contacto=T5.CodContacto AND T4.Id_Grupo=T5.CodGrupo AND T1.CodDepartamento=T3.Id_Departamento