CREATE VIEW VW_ventasproyectadasXconv (TotalVentas, encargofiduciario, Id_Proyecto, NomProyecto) AS SELECT T1.TotalVentas, T2.encargofiduciario, T4.Id_Proyecto, T4.NomProyecto FROM ProyectoProyeccionesVentasTotal T1, Convocatoria T2, ConvocatoriaProyecto T3, Proyecto T4 WHERE T1.codproyecto=T4.Id_Proyecto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T4.Id_Proyecto AND T4.CodEstado=7 AND T2.encargofiduciario=103937